mapper.select:
select 
	task, 
	senior_name, 
	senior_dept,
	junior_name, 
	junior_dept,
	startDate, 
	deadDate, 
	importance,
	complete
from todo
where junior_name = #{juniorName}
order by importance DESC, deadDate

	and task = #{task}


	and senior_name = #{seniorName}


	and startDate = #{startDate}


	and deadDate = #{deadDate}


	and importance = #{importance}
