mapper.select:
select 
	task, 
	senior_name, 
	startDate, 
	deadDate, 
	importance
from todo
where junior_name = #{juniorName}
order by importance,deadDate

	and task = #{task}


	and senior_name = #{seniorName}


	and startDate = #{startDate}


	and deadDate = #{deadDate}


	and importance = #{importance}
